mapper.insert:
    update ACCOUNT
	set VALUE = VALUE - #{VALUE}
	where ACCOUNT_NUM = #{ACCOUNT_NUM}

    INSERT INTO INOUT (
		ACCOUNT_NUM			
		, OTHER_NUM			
		, OTHER_BANK			
		, OTHER_NAME			
		, VALUE			
		, DATE
	)
	values(
		#{ACCOUNT_NUM}			
		, #{OTHER_NUM}			
		, #{OTHER_BANK}			
		, #{OTHER_NAME}			
		, #{VALUE}			
		, #{DATE}
	)
